import React, { useState, useEffect } from 'react';
import Section from './Section';
import Text from './Text';
import LineBreak from './LineBreak';
import { Document, Page } from 'react-pdf'; // Import react-pdf components
import ResumePDF from '../assets/documents/Resume.pdf'

function Resume() {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1); // Initial page

  function onDocumentLoadSuccess({ numPages }) {
    setNumPages(numPages);
  }

  function onDocumentError(error) {
    console.error('Error loading PDF:', error);
  }

  function handlePageChange(newPage) {
    setPageNumber(newPage);
  }

  useEffect(() => {
    // Don't open PDF in a new window anymore
  }, []);

  return (
    <div>
      <Section>
        <h1 className="text-5xl mb-3">
          Resume
        </h1>
        <LineBreak />
      </Section>

      <Section>
        <Text>
          Below you can find a built-in PDF viewer for my resume. You can navigate through the pages using the controls below.
        </Text>
      </Section>

      <Section>
        <Document
          file={ResumePDF} // Path to your resume PDF
          onLoadSuccess={onDocumentLoadSuccess}
          onError={onDocumentError}
        >
          {({ numPages }) => (
            <>
              <div>
                Page {pageNumber} of {numPages}
              </div>
              <Page pageNumber={pageNumber} width="100%" />
            </>
          )}
        </Document>
        <div className="pagination">
          <button
            disabled={pageNumber === 1}
            onClick={() => handlePageChange(pageNumber - 1)}
          >
            Previous Page
          </button>
          <button
            disabled={pageNumber === numPages}
            onClick={() => handlePageChange(pageNumber + 1)}
          >
            Next Page
          </button>
        </div>
      </Section>
    </div>
  );
}

export default Resume;
