import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";

const Contact = () => {
  const form = useRef();
  const [isSent, setIsSent] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_hbdflos",
        "template_adfsmv4",
        form.current,
        "fHmr2B60Sq1RmbNtN"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
          setIsSent(true);
          form.current.reset();
          setTimeout(() => {
            setIsSent(false);
          }, 5000); // Reset success message after 5 seconds
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <div className="flex max-w-lg mx-auto">
      <div className="w-1/2 space-y-4">
        <h2 className="text-lg font-bold">Reach Out Via Socials</h2>
        <ul>
          <li><a href="#">Email</a></li>
          <li><a href="#">GitHub</a></li>
          <li><a href="#">LinkedIn</a></li>
        </ul>
      </div>
      <div className="w-1/2 space-y-4 pl-8">
        <h2 className="text-lg font-bold">Or Fill Out This Form</h2>
        {isSent && (
          <p className="text-green-500">Message sent successfully!</p>
        )}
        <form ref={form} onSubmit={sendEmail}>
        <div>
          <label htmlFor="user_name" className="block mb-1">
            Name
          </label>
          <input
            type="text"
            id="user_name"
            name="user_name"
            className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-green-500 text-black"
          />
        </div>
        <div>
          <label htmlFor="user_email" className="block mb-1">
            Email
          </label>
          <input
            type="email"
            id="user_email"
            name="user_email"
            className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-green-500 text-black"
          />
        </div>
        <div>
          <label htmlFor="message" className="block mb-1">
            Message
          </label>
          <textarea
            id="message"
            name="message"
            rows="4"
            className="w-full px-3 py-2 border border-gray-300 rounded focus:outline-none focus:border-green-500 text-black"
          ></textarea>
        </div>
        <div>
          <button
            type="submit"
            className="bg-orange-500 hover:bg-orange-600 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
          >
            Send
          </button>
        </div>
        </form>
      </div>
    </div>
  );
};

export default Contact;
