import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import Section from './Section';
import Text from './Text';
import Link from './Link';

const Contact = () => {
  const form = useRef();
  const [isSent, setIsSent] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs
      .sendForm(
        "service_hbdflos",
        "template_adfsmv4",
        form.current,
        "fHmr2B60Sq1RmbNtN"
      )
      .then(
        (result) => {
          console.log(result.text);
          console.log("message sent");
          setIsSent(true);
          form.current.reset();
          setTimeout(() => {
            setIsSent(false);
          }, 5000); // Reset success message after 5 seconds
        },
        (error) => {
          console.log(error.text);
        }
      );
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', maxWidth: '80rem', margin: 'auto', padding: '1.5rem', gap: '2rem' }}>
      <div style={{ width: '100%', flex: '1 1 33%', gap: '1rem' }}>
        <h2 style={{ fontSize: '1.125rem', fontWeight: 'bold' }}>Reach Out Via Socials</h2>
        <Section>
          <Text style={{ fontSize: '1.125rem' }}>
            .socials {"{"}
            <br />
            &nbsp;&nbsp;email: <Link targetBlank href="mailto:marcgavvid@gmail.com">marcgavvid@gmail.com</Link>;
            <br />
            &nbsp;&nbsp;github: <Link targetBlank href="https://github.com/MarcVidalCodes">MarcVidalCodes</Link>;
            <br />
            &nbsp;&nbsp;linkedin: <Link targetBlank href="https://www.linkedin.com/in/marc-vidal-979148278/">marc-vidal-979148278</Link>;
            <br />
            {"}"}
          </Text>
        </Section>
      </div>
      <div style={{ display: 'none', width: '1px', backgroundColor: '#FFD700' }}></div>
      <div style={{ display: 'block', height: '1px', backgroundColor: '#FFD700' }}></div>
      <div style={{ width: '100%', flex: '1 1 67%', gap: '1rem', paddingLeft: '2rem' }}>
        <h2 style={{ fontSize: '1.125rem', fontWeight: 'bold' }}>Or Fill Out This Form</h2>
        {isSent && (
          <p style={{ color: '#22C55E' }}>Message sent successfully!</p>
        )}
        <form ref={form} onSubmit={sendEmail} style={{ gap: '1rem' }}>
          <div>
            <label htmlFor="user_name" style={{ display: 'block', marginBottom: '0.25rem', fontWeight: '600' }}>
              Name
            </label>
            <input
              type="text"
              id="user_name"
              name="user_name"
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #D1D5DB', borderRadius: '0.375rem', outline: 'none', color: 'black' }}
            />
          </div>
          <div>
            <label htmlFor="user_email" style={{ display: 'block', marginBottom: '0.25rem', fontWeight: '600' }}>
              Email
            </label>
            <input
              type="email"
              id="user_email"
              name="user_email"
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #D1D5DB', borderRadius: '0.375rem', outline: 'none', color: 'black' }}
            />
          </div>
          <div>
            <label htmlFor="message" style={{ display: 'block', marginBottom: '0.25rem', fontWeight: '600' }}>
              Message
            </label>
            <textarea
              id="message"
              name="message"
              rows="4"
              style={{ width: '100%', padding: '0.5rem', border: '1px solid #D1D5DB', borderRadius: '0.375rem', outline: 'none', color: 'black' }}
            ></textarea>
          </div>
          <div style={{ display: 'flex', justifyContent: 'center' }}>
            <button
              type="submit"
              style={{
                backgroundColor: '#F97316',
                color: 'white',
                fontWeight: 'bold',
                padding: '0.5rem 1rem',
                borderRadius: '9999px',
                boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
                transition: 'all 0.3s ease-in-out',
                transform: 'scale(1)',
              }}
              onMouseEnter={(e) => {
                e.target.style.backgroundColor = '#EA580C';
                e.target.style.transform = 'scale(1.05)';
              }}
              onMouseLeave={(e) => {
                e.target.style.backgroundColor = '#F97316';
                e.target.style.transform = 'scale(1)';
              }}
            >
              Send
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default Contact;
