import React, { useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import resumePDF from '../assets/documents/Resume.pdf'; // Import the PDF file
import MarkdownButton from './MarkdownButton';
import Section from './Section';
import MarkdownHeader from './MarkdownHeader';
import LineBreak from './LineBreak';
import Comment from './Comment';


const Resume = () => {
  const [numPages, setNumPages] = useState(null);

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <Section>
        <MarkdownHeader headingLevel="2" text="My Resume" className="text-xl md:text-3xl" />
        <LineBreak />
      </Section>
      <Section>
        <Comment>Or click the link below to download my resume:</Comment>
        <MarkdownButton as="a" href={resumePDF} target="_blank" rel="noreferrer">
          [Download Resume]
        </MarkdownButton>
      </Section>
      <div className="pdf-container">
        <Document
          file={resumePDF} // Use the imported PDF file
          onLoadSuccess={onDocumentLoadSuccess}
        >
          {Array.from(new Array(numPages), (el, index) => (
            <Page key={index} pageNumber={index + 1} />
          ))}
        </Document>
      </div>
      <style>{`
        .pdf-container {
          display: flex;
          justify-content: center;
          margin-top: 20px;
          margin-right:2rem;
        }

        @media screen and (max-width: 768px) {
          .pdf-container {
            width: 150%;
          }
        }
      `}</style>
    </div>
  );
};

export default Resume;
