// src/components/letterofrec.js
import React, { useState, useEffect, useRef } from 'react';
import Section from './Section';
import MarkdownHeader from './MarkdownHeader';
import Text from './Text';
import referenceLetter from '../assets/documents/LetterOfRecommendation.pdf';

function ReferenceLetters() {
  const [key, setKey] = useState(0);
  const objectRef = useRef(null);

  // Force re-render when component mounts
  useEffect(() => {
    setKey(prev => prev + 1);
  }, []);

  // Also force re-render if the object fails to load
  const handleError = () => {
    setTimeout(() => {
      setKey(prev => prev + 1);
    }, 100);
  };

  return (
    <div>
      <Section>
        <MarkdownHeader headingLevel="1" text="Reference Letters" className="text-3xl md:text-5xl mb-4" />
        <Text className="mb-4">Letters of recommendation from my professional experiences.</Text>

        <div className="flex flex-col items-center">
          <div className="w-full max-w-4xl h-screen bg-white rounded border border-gray-300">
            <object
              key={key} // Force re-render with key
              ref={objectRef}
              data={`${referenceLetter}#toolbar=1&navpanes=1&scrollbar=1`}
              type="application/pdf"
              width="100%"
              height="100%"
              className="rounded"
              onError={handleError}
            >
              <div className="flex flex-col items-center justify-center h-full text-center p-8">
                <p className="text-gray-700 mb-4">
                  Your browser doesn't support PDF viewing.
                </p>
                <a 
                  href={referenceLetter} 
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-block px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded transition-colors"
                >
                  Open PDF in New Tab
                </a>
              </div>
            </object>
          </div>

          <div className="mt-4 flex space-x-4">
            <button 
              onClick={() => setKey(prev => prev + 1)}
              className="inline-block px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded transition-colors"
            >
              Reload PDF
            </button>
            <a 
              href={referenceLetter} 
              download="LetterOfRecommendation.pdf"
              className="inline-block px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded transition-colors"
            >
              Download PDF
            </a>
          </div>
        </div>
      </Section>
    </div>
  );
}

export default ReferenceLetters;
