import React, { useEffect, useState } from 'react';
import { Document, Page, pdfjs } from 'react-pdf';
import Section from './Section';
import Text from './Text';
import LineBreak from './LineBreak';
import ResumePDF from '../assets/documents/Resume.pdf';

function Resume() {
  const [numPages, setNumPages] = useState(null);
  const [pageNumber, setPageNumber] = useState(1);

  pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;

  const onDocumentLoadSuccess = ({ numPages }) => {
    setNumPages(numPages);
  };

  useEffect(() => {
    // Automatically open the resume PDF when the component is rendered
    window.open(ResumePDF, '_blank', 'noopener,noreferrer');
  }, []);

  return (
    <div>
      <Section>
        <h1 className="text-5xl mb-3">Resume</h1>
        <LineBreak />
      </Section>

      <Section>
        <Text>
          Below you can find my resume with detailed information on my background and previous work.
        </Text>
      </Section>

      <Section>
        <Text>You can view my resume directly here.</Text>
      </Section>

      <Section>
        <Document file={ResumePDF} onLoadSuccess={onDocumentLoadSuccess}>
          <Page pageNumber={pageNumber} />
        </Document>
      </Section>
    </div>
  );
}

export default Resume;
