import React, { useEffect } from 'react';
import Tab from './Tab';
import useTabContext from '../../hooks/useTabContext';
import useHorizontalScroll from '../../hooks/useHorizontalScroll';

function TabBar() {
  const scrollRef = useHorizontalScroll();
  const tabs = useTabContext();
  const selectedTab = tabs.current ? tabs.current.name : null

  useEffect(() => {
    if (selectedTab) {
      const tab = document.querySelector(`[data-tab="${selectedTab}"]`);
      tab.scrollIntoView();
    }
  }, [selectedTab]);

  if (tabs.tabList.length < 1) return;

  return (
    <div ref={scrollRef} className="bg-dark-600 flex font-medium text-lg h-[50px] grow select-none overflow-x-auto scrollbar-hide border-r-2 border-dark-400">
      {tabs.tabList.map((tab) => (<Tab tab={tab} key={tab.name} />))}
    </div>
  )
}

export default TabBar;
