import { createContext, useContext, useState } from 'react';

const TabContext = createContext();

export function useTabContext() {
  return useContext(TabContext);
}

export function TabProvider({ children, initialTabs = [] }) {
  const [tabs, setTabs] = useState(initialTabs);
  const [current, setCurrent] = useState(initialTabs[0] || null);

  const switchTabs = (newTab) => {
    const isJSXTab = newTab.extension === 'jsx';
    const existingJSXTab = tabs.find(tab => tab.extension === 'jsx');

    if (isJSXTab && existingJSXTab) {
      setTabs(tabs.map(tab => tab.extension === 'jsx' ? newTab : tab));
      setCurrent(newTab);
    } else {
      setTabs([...tabs, newTab]);
      setCurrent(newTab);
    }
  };

  const removeTab = (tabToRemove) => {
    const updatedTabs = tabs.filter(tab => tab !== tabToRemove);
    setTabs(updatedTabs);
    if (current === tabToRemove) {
      setCurrent(updatedTabs[0] || null);
    }
  };

  return (
    <TabContext.Provider value={{ tabs, current, switchTabs, removeTab }}>
      {children}
    </TabContext.Provider>
  );
}
