// src/components/letterofrec.js
import React from 'react';
import Section from './Section';
import MarkdownHeader from './MarkdownHeader';
import Text from './Text';
import referenceLetter from '../assets/documents/LetterOfRecommendation.pdf';

function ReferenceLetters() {
  return (
    <div>
      <Section>
        <MarkdownHeader headingLevel="1" text="Reference Letter" className="text-3xl md:text-5xl mb-4" />
        <Text className="mb-4">Letter of recommendation written by DecisivEdge president</Text>

        <div className="flex flex-col items-center">
          <div className="w-full max-w-4xl h-screen bg-white rounded border border-gray-300">
            <object
              data={referenceLetter}
              type="application/pdf"
              width="100%"
              height="100%"
              className="rounded"
            >
              <div className="flex flex-col items-center justify-center h-full text-center p-8">
                <p className="text-gray-700 mb-4">
                  Your browser doesn't support PDF viewing.
                </p>
                <a 
                  href={referenceLetter} 
                  download="LetterOfRecommendation.pdf"
                  className="inline-block px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded transition-colors"
                >
                  Download PDF
                </a>
              </div>
            </object>
          </div>

          <div className="mt-4">
            <a 
              href={referenceLetter} 
              download="LetterOfRecommendation.pdf"
              className="inline-block px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded transition-colors"
            >
              Download PDF
            </a>
          </div>
        </div>
      </Section>
    </div>
  );
}

export default ReferenceLetters;
